BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "source" (
	"source_id"	INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,
	"source_date"	INTEGER
);
CREATE TABLE IF NOT EXISTS "amendes" (
	"amendes_id"	INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,
	"amendes_source_id"	INTEGER,
	"amendes_montant"	INTEGER,
	"amendes_type"	TEXT,
	"amendes_franche_verite"	TEXT,
	"amendes_transcription"	TEXT
);
CREATE TABLE IF NOT EXISTS "personnes" (
	"personnes_id"	INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,
	"personnes_amendes_id"		INTEGER,
	"personnes_nom"	TEXT,
	"personnes_prenom"	TEXT
);
COMMIT;